function closestNode(nodes, unvisitedNodes) {
  let currentClosest, index;

  for (let i = 0; i < unvisitedNodes.length; i++) {
    if (!currentClosest || currentClosest.totalDistance > nodes[unvisitedNodes[i]].totalDistance) {
