import React, {Component} from 'react';
import Node from "./Node/Node"
import "./pfv.css";
import {dijkstra,shortestPath} from"../algorithms/dijkstra.js"
const START_NODE_ROW = 10;
const START_NODE_COL = 15;
const END_NODE_ROW = 10;
const END_NODE_COL = 35;

export default class PFV extends Component{
    static defaultProps = {
        START_NODE_ROW : START_NODE_ROW,
        START_NODE_COL : START_NODE_COL,
        END_NODE_ROW : END_NODE_ROW,
        END_NODE_COL : END_NODE_COL,
        nrows:50,
        ncols:20,
    }
    constructor(props){
        super(props);
        this.state={
            grid: [],
            isMouseDown : false,
        };
        this.visualizeDijkstra=this.visualizeDijkstra.bind(this);
    }
    componentDidMount(){
        const grid=getGrid();
        this.setState({grid});
    }
    visualizeDijkstra(){
        const {grid} = this.state;
        const startNode = grid[this.props.START_NODE_ROW][this.props.START_NODE_COL];
        const endNode = grid[this.props.END_NODE_ROW][this.props.END_NODE_COL];
        const visitedOrder = dijkstra(grid, startNode, endNode);
        const path = shortestPath(endNode);
        animateVisitOrder(visitedOrder,path);
    }
    toggleWall(row, col){
        const newgrid = this.state.grid;
        const node = newgrid[row][col];
        const newnode = {
            ...node,
            isWall:!node.isWall,
        }
        newgrid[node.row][node.col]=newnode;
        this.setState({grid:newgrid});
    }
    handleMouseDown(row, col) {
        if(!this.state.isMouseDown)
            this.setState({isMouseDown:true});
        this.toggleWall(row, col);
    }
    handleMouseEnter(row, col) {
        if(this.state.isMouseDown)
            this.toggleWall(row, col);
    }
    

    handleMouseUp(){
        this.setState({isMouseDown:false});
    }
    makeGrid(){
        let grid=[];
        for(let x =0;x<20;x++){
            let row=[];
            for(let y = 0;y<50;y++){
                row.push(
                    <Node
                        key = {`${y}-${x}`}
                        {...createNode(x,y)}
                        onMouseDown={(row, col) => this.handleMouseDown(row, col)}
                        onMouseEnter={(row, col) => this.handleMouseEnter(row, col)}
                        onMouseUp={() => this.handleMouseUp()}
                    />
                );
            }
            grid.push(<tr key={x}>{row}</tr>)
        }
        return (
            <table className='Grid'>
                <tbody>{grid}</tbody>
            </table>
        )
    }     
    render(){
        const {grid} = this.state;
        return(
            <div>
                {this.makeGrid()}
            </div>
            /*<>
                <button onClick={this.visualizeDijkstra}>
                    Visualize
                </button>
                <div className="grid">
                    {grid.map((row,rowIdx)=>{
                        return (
                            <div classname='row' key ={rowIdx} >
                                {row.map((node,nodeIdx)=>{
                                    return (
                                        <Node
                                            key = {`${rowIdx}-${nodeIdx}`}
                                            {...node}
                                            onMouseDown={(row, col) => this.handleMouseDown(row, col)}
                                            onMouseEnter={(row, col) => this.handleMouseEnter(row, col)}
                                            onMouseUp={() => this.handleMouseUp()}
                                        />
                                    );
                                })}
                            </div>
                        ); 
                    })}
                </div>
            </>*/
        );
    }
}
//onClick ={() =>{console.log(`Hi from ${rowIdx}`);}}
function getGrid() {
    const grid = [];
    for(let row=0;row<20;row++){
        const currentRow = [];
        for(let col=0;col<50;col++){
            currentRow.push(createNode(col,row));
        }
        grid.push(currentRow);
    }
    return grid;
}

function createNode(col,row){
    return{
        col,
        row,
        isStart:row === START_NODE_ROW && col===START_NODE_COL,
        isEnd: row === END_NODE_ROW && col === END_NODE_COL,
        distance: Infinity,
        isVisited: false,
        isWall: false,
        previousNode: null,
    };
}
function animateVisitOrder(visitedOrder, path) {
    for (let i = 0; i <= visitedOrder.length; i++) {
      if (i === visitedOrder.length) {
        setTimeout(() => {
          animatePath(path);
        }, 10 * i);
        return;
      }
      setTimeout(() => {
        const node = visitedOrder[i];
        document.getElementById(`${node.row}-${node.col}`).className ='node node-visited';
      }, 10 * i);
    }
}
function animatePath(path) {
    for (let i = 0; i < path.length; i++) {
      setTimeout(() => {
        const node = path[i];
        document.getElementById(`${node.row}-${node.col}`).className =
          'node node-shortest-path';
      }, 50 * i);
    }
}
